#include <iostream>

const int N = 1003;

int length(int t[N]) {
    int max_length = -1;
    int element_sum = t[0];
    int index_sum = 0;
    int current_length = (element_sum == 0) ? 1 : 0;
    int i = 1;
    int prev = t[i];

    int beginning = t[0];

    while (i < N) {

        element_sum += t[i];
        index_sum += i;

        if (element_sum > index_sum || t[i] <= prev) {
            element_sum = t[i];
            index_sum = i;
            current_length = (element_sum == index_sum) ? 1 : 0;
            if (max_length <= 0) max_length = current_length;

            beginning = t[i];
        }

        if (element_sum <= index_sum) {
            current_length++; 
        }

        if (element_sum == index_sum) {
            if (max_length < current_length) {
                max_length = current_length;
                std::cout << i;
            }
        }

        std::cout << element_sum << " " << index_sum << " " << current_length << " " << beginning << " " << std::endl;

        prev = t[i];
        i++;
    }

    
    if (element_sum == index_sum) max_length = current_length;

    return (max_length < 0) ? 0 : max_length;
}


